import React, { useState, useEffect } from 'react';

const translations = {
  en: { welcome: "Welcome!", priceLabel: "Price", selectLang: "Language", selectCurrency: "Currency" },
  es: { welcome: "¡Bienvenido!", priceLabel: "Precio", selectLang: "Idioma", selectCurrency: "Moneda" },
  fr: { welcome: "Bienvenue!", priceLabel: "Prix", selectLang: "Langue", selectCurrency: "Devise" },
};

const exchangeRates = {
  USD: 1,
  EUR: 0.93,
  GBP: 0.80,
};

const CurrencyLanguageSwitcher = () => {
  const [language, setLanguage] = useState('en');
  const [currency, setCurrency] = useState('USD');
  const [price, setPrice] = useState(100); // Base price in USD

  useEffect(() => {
    const savedLang = localStorage.getItem('language');
    const savedCurr = localStorage.getItem('currency');
    if (savedLang) setLanguage(savedLang);
    if (savedCurr) setCurrency(savedCurr);

    // Auto-detect country
    fetch('https://ipapi.co/json/')
      .then((res) => res.json())
      .then((data) => {
        if (!savedLang && data.country_code === 'FR') setLanguage('fr');
        else if (!savedLang && data.country_code === 'ES') setLanguage('es');
        if (!savedCurr && data.currency) setCurrency(data.currency === 'EUR' ? 'EUR' : 'USD');
      });
  }, []);

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
    localStorage.setItem('language', e.target.value);
  };

  const handleCurrencyChange = (e) => {
    setCurrency(e.target.value);
    localStorage.setItem('currency', e.target.value);
  };

  const localized = translations[language];
  const convertedPrice = (price * exchangeRates[currency]).toFixed(2);

  return (
    <div>
      <h2>{localized.welcome}</h2>
      <p>{localized.priceLabel}: {convertedPrice} {currency}</p>

      <label>{localized.selectLang}:</label>
      <select value={language} onChange={handleLanguageChange}>
        <option value="en">English</option>
        <option value="es">Español</option>
        <option value="fr">Français</option>
      </select>

      <br /><br />

      <label>{localized.selectCurrency}:</label>
      <select value={currency} onChange={handleCurrencyChange}>
        <option value="USD">USD ($)</option>
        <option value="EUR">EUR (€)</option>
        <option value="GBP">GBP (£)</option>
      </select>
    </div>
  );
};

export default CurrencyLanguageSwitcher;
