// src/components/TravelBundles.js
import React, { useState } from 'react';

const defaultBundles = [
  {
    id: 1,
    name: "Beach Escape",
    items: ["Flight to Goa", "3 Nights Hotel", "Beach Tour"],
    originalPrice: 25000,
    discount: 0.1, // 10%
  },
  {
    id: 2,
    name: "Mountain Adventure",
    items: ["Flight to Manali", "2 Nights Hotel", "Snow Trekking"],
    originalPrice: 30000,
    discount: 0.15, // 15%
  }
];

const TravelBundles = () => {
  const [bundles] = useState(defaultBundles);

  const handleShare = (bundleName) => {
    const url = `${window.location.href}?bundle=${bundleName.replace(/\s+/g, '-')}`;
    navigator.clipboard.writeText(url);
    alert("Link copied to clipboard!");
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>üåê Travel Package Bundles</h2>
      {bundles.map(bundle => {
        const discountedPrice = bundle.originalPrice * (1 - bundle.discount);
        return (
          <div key={bundle.id} style={{ border: '1px solid gray', margin: '10px 0', padding: '10px' }}>
            <h3>{bundle.name}</h3>
            <ul>
              {bundle.items.map((item, idx) => (
                <li key={idx}>{item}</li>
              ))}
            </ul>
            <p><s>‚Çπ{bundle.originalPrice}</s> <strong>‚Çπ{discountedPrice.toFixed(0)}</strong> ({bundle.discount * 100}% off)</p>
            <button onClick={() => handleShare(bundle.name)}>üì§ Share</button>
          </div>
        );
      })}
    </div>
  );
};

export default TravelBundles;
