import React, { useState, useEffect } from 'react';
import './App.css';
import CurrencyLanguageSwitcher from './components/CurrencyLanguageSwitcher';
import ReviewRating from './components/ReviewRating';
import Wishlist from './components/Wishlist';

function App() {
  const [reviews, setReviews] = useState([]);

  const addReview = (newReview) => {
    const updatedReviews = [...reviews, newReview];
    setReviews(updatedReviews);
    localStorage.setItem('reviews', JSON.stringify(updatedReviews));
  };

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('reviews')) || [];
    setReviews(stored);
  }, []);

  return (
    <div className="App">
      <h1>üåç Travel Project - Internship Tasks</h1>

      <section>
        <h2>1. Wishlist Feature</h2>
        <Wishlist />
      </section>

      <section>
        <h2>2. Review & Rating System</h2>
        <ReviewRating onSubmit={addReview} />

        <div style={{ marginTop: '20px' }}>
          <h3>All Reviews:</h3>
          {reviews.length === 0 ? (
            <p>No reviews yet.</p>
          ) : (
            <ul>
              {reviews.map((r, idx) => (
                <li key={idx}>
                  <strong>{r.rating}‚òÖ</strong> - {r.review}
                </li>
              ))}
            </ul>
          )}
        </div>
      </section>

      <section>
        <h2>3. Currency & Language</h2>
        <CurrencyLanguageSwitcher />
      </section>
    </div>
  );
}

export default App;
